package condistionstatmets;

public class Break {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		 //using for loop  
	    for(int i=0;i<=10;i++){  
	        if(i==7){  
	            //breaking the loop  
	            break;  
	        }  
	        System.out.println(i);  
	    }  
	}

}
