package condistionstatmets;

public class Continu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  for(int i=0;i<=15;i++){  
		        if(i==5){  
		            //using continue statement  
		            continue;//it will skip the rest statement  
		        }  
		        System.out.println(i);  
		    }  

	}

}
